/** inverter data modbus request data command in hex
* a5    |1700       |1045|0000|xxxxxxxx|020000000000000000000000000000|0103 0000 0028 xxxx|xx       |15
* a5    |1700       |1045|0000|xxxxxxxx|020000000000000000000000000000|0103 0105 0010 xxxx|xx       |15
* begin |data length| 2b | 2b |   SN   |            15b               |   modbus command  |checksum |end
*/

const requestNr = flow.get('requestNr') > 255 ? 0 : flow.get('requestNr') || 0;
const responseNr = flow.get('responseNr') || 0;
const serialNumber = env.get('loggerSN');   // in hex it is 32LE
const firstReg = parseInt(env.get('firstReg'), 16);
const lastReg = parseInt(env.get('lastReg'), 16);

const frame = Buffer.alloc(36);
frame.writeUInt8(0xa5, 0);  // frame begin
frame.writeUInt16BE(0x1700, 1); // data length
frame.writeUInt16BE(0x1045, 3); // control code
frame.writeUInt8(requestNr, 5); // request number
frame.writeUInt8(responseNr, 6); // server response number
frame.writeUInt32LE(serialNumber, 7); // logger SN
frame.write('0200000000000000000000000000', 11, 15, 'hex');  // data 15 bytes
// modbus command
frame.writeUInt16BE(0x0103, 26) // get data code
// 1st register range
frame.writeUInt16BE(firstReg, 28) // first register
frame.writeUInt16BE(lastReg - firstReg + 1, 30) // number of registers to read
// 2nd register range
// frame.writeUInt16BE(0x0105, 28) // reg start
// frame.writeUInt16BE(0x0010, 30) // number of registers to read
// crc
let commandCrcModbus = crc16modbus(frame.slice(26, 32)); //! uInt16LE
frame.writeUInt16LE(commandCrcModbus, 32);  //command crc

frame.writeUInt8(frameChecksum(frame), 34);  //frame checksum
frame.writeUInt8(0x15, 35);  // frame end
// node.warn(frame.toString('hex'));

msg = {
    payload: frame,
    topic: 'raw request'
};
flow.set('waitingForResponse', true);
return msg;

function frameChecksum(frame) {
    frame = frame.subarray(1, -2);
    let sum = 0x0;
    for (const byte of frame) {
        sum += byte;
    }
    sum = sum % 256;
    return sum;
}

// let waitingForResponse = flow.get('waitingForResponse');
const data = decodeData(msg.payload.data); // decode response data
if (data !== null) {
    flow.set('waitingForResponse', false);
    msg.topic = 'decoded data';
    if (typeof msg.payload.extraData !== 'undefined') {
        msg.debug.extraData = msg.payload.extraData;
        // return [[msg, {
        //     payload: msg.payload.extraData,
        //     topic: 'extra data'
        // }]];
    }
    msg.payload = data;
    return msg;
}
msg.payload = msg.payload.data; // decode failed send raw buffer
if (!flow.get('waitingForResponse')) {
    msg.topic = 'unexpected data';
} else {
    flow.set('waitingForResponse', false);
    msg.topic = 'unknown data';
}
return msg;

function decodeData(inputBuffer) { // real time data(Function Code 0x03) response
    const data = {};
    try {
        const buffer = inputBuffer.response;
        const registersDataLength = buffer[2]; // first 3 bytes are slave address, function code, data length
        const registersBuffer = buffer.subarray(3, 3 + registersDataLength); // registers data begins at 3rd byte

        // inverter status
        data.inverterStatus = decodeInverterStatus(registersBuffer.readUInt16BE(0x00));
        //errors
        data.errors = {};
        data.errors.fault1 = registersBuffer.readUInt16BE(0x01 * 2);
        data.errors.fault2 = registersBuffer.readUInt16BE(0x02 * 2);
        data.errors.fault3 = registersBuffer.readUInt16BE(0x03 * 2);
        data.errors.fault4 = registersBuffer.readUInt16BE(0x04 * 2);
        data.errors.fault5 = registersBuffer.readUInt16BE(0x05 * 2);

        //PV Input Message
        data.PV = {};
        data.PV.string1 = {};
        data.PV.string1.voltage = registersBuffer.readUInt16BE(0x06 * 2) / 10;   // V
        data.PV.string1.current = registersBuffer.readInt16BE(0x07 * 2) / 100;   // A
        data.PV.string1.power = registersBuffer.readUInt16BE(0x0a * 2) * 10;     // W (/ 100 -> kW)
        data.PV.string2 = {};
        data.PV.string2.voltage = registersBuffer.readUInt16BE(0x08 * 2) / 10;   // V
        data.PV.string2.current = registersBuffer.readInt16BE(0x09 * 2) / 100;   // A
        data.PV.string2.power = registersBuffer.readUInt16BE(0x0b * 2) * 10;     // W (/ 100 -> kW)

        // Output Grid Message
        data.grid = {};
        data.grid.activePower = registersBuffer.readUInt16BE(0x0c * 2) * 10;     // W (/ 100 -> kW)
        data.grid.reactivePower = registersBuffer.readInt16BE(0x0d * 2) / 100;   // kVar
        data.grid.frequency = registersBuffer.readUInt16BE(0x0e * 2) / 100;      // Hz
        data.grid.L1_V = registersBuffer.readUInt16BE(0x0f * 2) / 10;            // V
        data.grid.L1_A = registersBuffer.readUInt16BE(0x10 * 2) / 100;           // A
        data.grid.L2_V = registersBuffer.readUInt16BE(0x11 * 2) / 10;            // V
        data.grid.L2_A = registersBuffer.readUInt16BE(0x12 * 2) / 100;           // A
        data.grid.L3_V = registersBuffer.readUInt16BE(0x13 * 2) / 10;            // V
        data.grid.L3_A = registersBuffer.readUInt16BE(0x14 * 2) / 100;           // A

        // Inverter Generation message
        data.production = {};
        data.production.TotalEnergy = registersBuffer.readUInt32BE(0x15 * 2);        // kW
        data.production.TotalTime = registersBuffer.readUInt32BE(0x17 * 2);          // hours
        data.production.TodayEnergy = registersBuffer.readUInt16BE(0x19 * 2) / 100;  // kW (* 10 -> W)
        data.production.TodayTime = registersBuffer.readUInt16BE(0x1a * 2);          // minutes

        // Inverter inner message
        data.info = {};
        data.info.loggerTemp = registersBuffer.readInt16BE(0x1b * 2);        // °C
        data.info.inverterTemp = registersBuffer.readInt16BE(0x1c * 2);      // °C
        data.info.Vbus = registersBuffer.readUInt16BE(0x1d * 2) / 10;        // V
        data.info.PV1VCPU = registersBuffer.readUInt16BE(0x1e * 2) / 10;     // V
        data.info.PV1ACPU = registersBuffer.readUInt16BE(0x1f * 2) / 100;    // A
        data.info.countdownTime = registersBuffer.readUInt16BE(0x20 * 2);    // seconds
        data.info.inverterAlarmInfo = registersBuffer.readUInt16BE(0x21 * 2);
        data.info.inputMode = inputModeDecoder(registersBuffer.readUInt16BE(0x22 * 2));
        data.info.inverterInnerInfo = registersBuffer.readUInt16BE(0x23 * 2);
        data.info.PV1insulationResistance = registersBuffer.readUInt16BE(0x24 * 2);  // kOhm
        data.info.PV2insulationResistance = registersBuffer.readUInt16BE(0x25 * 2);   // kOhm
        data.info.cathode_groundInsulationImpedance = registersBuffer.readUInt16BE(0x26 * 2);  // KOhm
        data.info.countryCode = registersBuffer.readUInt16BE(0x27 * 2);

        data.timestamp = Math.floor(msg.timestamp / 1000); // reading timestamp in seconds
    } catch (error) {
        return null;
    };

    return data;
}

function decodeInverterStatus(status) {
    switch (status) {
        case 0x00:
            return `standby`;
        case 0x02:
            return `normal`;
        case 0x03:
            return `fault`;
        case 0x04:
            return `permanent`;

        default:
            return `0x${`0${(status.toString(16))}`.slice(-2)}`;
    }
}

function inputModeDecoder(mode) {
    switch (mode) {
        case 0x00:
            return 'parallel';
        case 0x01:
            return 'independent';

        default:
            return `0x${`0${(mode.toString(16))}`.slice(-2)}`;
    }
}

function country(countryCode) {

}

if (typeof msg.debug === 'undefined') msg.debug = {};
msg.debug.header = decodeHeader(msg.payload.header);
flow.set('requestNr', (msg.debug.header.requestNr >= 255) ? 1 : (msg.debug.header.requestNr + 1));
flow.set('responseNr', msg.debug.header.responseNr);
return msg;

function decodeHeader(buffer) {
    const header = {};
    header.dataLength = buffer.readUInt16LE(1);
    header.requestNr = buffer.readUInt8(5);
    header.responseNr = buffer.readUInt8(6);
    header.loggerSN = buffer.readUInt32LE(7);
    return header;
}

msg.payload = checkFrame(msg.payload);
msg.debug = {};
msg.debug.frame = msg.payload;
msg.topic = 'frame';
msg.timestamp = Date.now(); // timestamp in ms
return msg;

function checkFrame(frameBuffer) {
    const frame = {};
    const headerSize = 11;
    const footerSize = 2;
    const frameDataLength = frameBuffer.readUInt16LE(1);
    const calculatedLength = headerSize + frameDataLength + footerSize;
    if (frameBuffer.length !== calculatedLength) {
        if (frameBuffer[calculatedLength - 1] === 0x15) {
            frame.length = calculatedLength;
            frame.extraData = frameBuffer.subarray(calculatedLength + 1);
        } else {
            frame.length = frameBuffer.length;
        }
    } else {
        frame.length = calculatedLength;
    }
    frame.isValidV5frame = frameBuffer[0] === 0xA5 && frameBuffer[frame.length - 1] === 0x15;
    frame.buffer = frameBuffer;

    frame.header = frameBuffer.subarray(0, 11);

    if (frameDataLength > 14) {
        frame.data = {};
        const data = frameBuffer.subarray(headerSize, headerSize + frameDataLength);
        frame.data.unknown = data.subarray(0, 14); //TODO figure out what those 14 bytes are
        const response = data.subarray(14);
        frame.data.response = response;
        if (response.length > 3) {
            const registersDataLength = response[2]; // first 3 bytes are: slave address, function code, data length
            const calculatedCrc = crc16modbus(response.subarray(0, 3 + registersDataLength));
            const crc = response.readUInt16LE(3 + registersDataLength);
            frame.data.responseCrc = calculatedCrc === crc ? 'OK' : 'mismatch';
        }
    } else {
        frame.data = frameBuffer.subarray(headerSize, headerSize + frameDataLength);
    }

    frame.footer = frameBuffer.subarray(frame.length - 2, frame.length);

    const calculatedChecksum = frameChecksum(frameBuffer);
    const checksum = frameBuffer.readUInt8(headerSize + frameDataLength);
    frame.frameChecksum = calculatedChecksum === checksum ? 'OK' : 'mismatch';

    frame.timestamp = Math.floor(Date.now() / 1000); // unixtimestamp in seconds
    return frame;
}

function frameChecksum(frame) {
    frame = frame.subarray(1, -2);
    let sum = 0x0;
    for (const byte of frame) {
        sum += byte;
    }
    sum = sum % 256;
    return sum;
}
