/** inverter data modbus request data command in hex
* a5    |1700       |1045|0000|xxxxxxxx|020000000000000000000000000000|0103 0000 0028 xxxx|xx       |15
* a5    |1700       |1045|0000|xxxxxxxx|020000000000000000000000000000|0103 0105 0010 xxxx|xx       |15
* begin |data length| 2b | 2b |   SN   |            15b               |   modbus command  |checksum |end
*/

const requestNr = flow.get('requestNr') > 255 ? 0 : flow.get('requestNr') || 0;
const responseNr = flow.get('responseNr') || 0;
const serialNumber = env.get('loggerSN');   // in hex it is 32LE
const firstReg = parseInt(env.get('firstReg'), 16);
const lastReg = parseInt(env.get('lastReg'), 16);

const frame = Buffer.alloc(36);
frame.writeUInt8(0xa5, 0);  // frame begin
frame.writeUInt16BE(0x1700, 1); // data length
frame.writeUInt16BE(0x1045, 3); // control code
frame.writeUInt8(requestNr, 5); // request number
frame.writeUInt8(responseNr, 6); // server response number
frame.writeUInt32LE(serialNumber, 7); // logger SN
frame.writeUInt8(0x02, 11); // frame type
frame.writeUInt16LE(0x0000, 12); // sensor type
frame.writeUInt32LE(0x0000, 14); // total working time
frame.writeUInt32LE(0x0000, 18); // power on time
frame.writeUInt32LE(0x0000, 22); // offset time
// modbus command
frame.writeUInt16BE(0x0103, 26) // get data code
// 1st register range
frame.writeUInt16BE(firstReg, 28) // first register
frame.writeUInt16BE(lastReg - firstReg + 1, 30) // number of registers to read
// 2nd register range
// frame.writeUInt16BE(0x0105, 28) // reg start
// frame.writeUInt16BE(0x0010, 30) // number of registers to read
// crc
let commandCrcModbus = crc16modbus(frame.subarray(26, 32)); //! uInt16LE
frame.writeUInt16LE(commandCrcModbus, 32);  //command crc

frame.writeUInt8(frameChecksum(frame), 34);  //frame checksum
frame.writeUInt8(0x15, 35);  // frame end
// node.warn(frame.toString('hex'));

msg = {
    payload: frame,
    topic: 'raw request'
};
flow.set('waitingForResponse', true);
return msg;

function frameChecksum(frame) {
    frame = frame.subarray(1, -2);
    let sum = 0x0;
    for (const byte of frame) {
        sum += byte;
    }
    sum = sum % 256;
    return sum;
}

let decodedMsg = handleMsg(msg.payload);
decodedMsg.debug = msg.debug;
let extraMsg = null;
if (typeof msg.payload.extraData !== "undefined") {
    if (isFrameValid(msg.payload.extraData)) {
        log("info", "decoding extra data");
        if (decodedMsg.topic !== "decoded data") { flow.set("waitingForResponse", true); }
        extraMsg = handleMsg(msg.payload.extraData);
    }
}
return [[decodedMsg, extraMsg]];

function handleMsg(input) {
    const message = { payload: null };
    if (isFrameValid(input)) {
        message.payload = decodeData(input.data); // decode response data
    } else { log("warn", "invalid data, skipping decoding"); }
    if (message.payload !== null) {
        flow.set("waitingForResponse", false);
        message.topic = "decoded data";
    } else { // decode failed send raw buffer
        message.payload = input.data;
        if (!flow.get("waitingForResponse")) {
            message.topic = "unexpected data";
        } else {
            flow.set("waitingForResponse", false);
            message.topic = "unknown data";
        }
    }
    return message;
}

function isFrameValid(frame) {
    return frame.isValidV5frame && frame.frameChecksum === "OK" && frame.data.modbusFrameCrc === "OK";
}

function decodeData(inputBuffer) { // real time data(Function Code 0x03) response
    const data = {};
    try {
        const solarman = inputBuffer.solarmanHeader;

        const modbusBuffer = inputBuffer.modbusFrame;
        const registersDataLength = modbusBuffer[2]; // first 3 bytes are slave address, function code, data length
        const modbusRegisters = modbusBuffer.subarray(3, 3 + registersDataLength); // registers data begins at 3rd byte

        // inverter status
        data.inverterStatus = decodeInverterStatus(modbusRegisters.readUInt16BE(0x00));
        //errors
        data.errors = errorDecoder(modbusRegisters.subarray(0x01 * 2, 0x06 * 2));

        //PV Input Message
        data.PV = {};
        data.PV.string1 = {};
        data.PV.string1.voltage = modbusRegisters.readUInt16BE(0x06 * 2) / 10;   // V
        data.PV.string1.current = modbusRegisters.readInt16BE(0x07 * 2) / 100;   // A
        data.PV.string1.power = modbusRegisters.readUInt16BE(0x0a * 2) * 10;     // W (/ 100 -> kW)
        data.PV.string2 = {};
        data.PV.string2.voltage = modbusRegisters.readUInt16BE(0x08 * 2) / 10;   // V
        data.PV.string2.current = modbusRegisters.readInt16BE(0x09 * 2) / 100;   // A
        data.PV.string2.power = modbusRegisters.readUInt16BE(0x0b * 2) * 10;     // W (/ 100 -> kW)
        log("debug", "PV Input data successfully decoded");

        // Output Grid Message
        data.grid = {};
        data.grid.activePower = modbusRegisters.readUInt16BE(0x0c * 2) * 10;     // W (/ 100 -> kW)
        data.grid.reactivePower = modbusRegisters.readInt16BE(0x0d * 2) / 100;   // kVar
        data.grid.frequency = modbusRegisters.readUInt16BE(0x0e * 2) / 100;      // Hz
        data.grid.L1_V = modbusRegisters.readUInt16BE(0x0f * 2) / 10;            // V
        data.grid.L1_A = modbusRegisters.readUInt16BE(0x10 * 2) / 100;           // A
        data.grid.L2_V = modbusRegisters.readUInt16BE(0x11 * 2) / 10;            // V
        data.grid.L2_A = modbusRegisters.readUInt16BE(0x12 * 2) / 100;           // A
        data.grid.L3_V = modbusRegisters.readUInt16BE(0x13 * 2) / 10;            // V
        data.grid.L3_A = modbusRegisters.readUInt16BE(0x14 * 2) / 100;           // A
        log("debug", "Output Grid data successfully decoded");

        // Inverter Generation message
        data.production = {};
        data.production.TotalEnergy = modbusRegisters.readUInt32BE(0x15 * 2);        // kW
        data.production.TotalTime = modbusRegisters.readUInt32BE(0x17 * 2);          // hours
        data.production.TodayEnergy = modbusRegisters.readUInt16BE(0x19 * 2) / 100;  // kW (* 10 -> W)
        data.production.TodayTime = modbusRegisters.readUInt16BE(0x1a * 2);          // minutes
        log("debug", "Inverter Generation data successfully decoded");

        // Inverter inner message
        data.info = {};
        data.info.loggerTemp = modbusRegisters.readInt16BE(0x1b * 2);        // °C
        data.info.inverterTemp = modbusRegisters.readInt16BE(0x1c * 2);      // °C
        data.info.Vbus = modbusRegisters.readUInt16BE(0x1d * 2) / 10;        // V
        data.info.PV1VCPU = modbusRegisters.readUInt16BE(0x1e * 2) / 10;     // V
        data.info.PV1ACPU = modbusRegisters.readUInt16BE(0x1f * 2) / 100;    // A
        data.info.countdownTime = modbusRegisters.readUInt16BE(0x20 * 2);    // seconds
        data.info.inverterAlarmInfo = modbusRegisters.readUInt16BE(0x21 * 2);
        data.info.inputMode = inputModeDecoder(modbusRegisters.readUInt16BE(0x22 * 2));
        data.info.inverterInnerInfo = modbusRegisters.readUInt16BE(0x23 * 2);
        data.info.PV1insulationResistance = modbusRegisters.readUInt16BE(0x24 * 2);  // kOhm
        data.info.PV2insulationResistance = modbusRegisters.readUInt16BE(0x25 * 2);   // kOhm
        data.info.cathode_groundInsulationImpedance = modbusRegisters.readUInt16BE(0x26 * 2);  // KOhm
        data.info.country = countryDecoder(modbusRegisters.readUInt16BE(0x27 * 2));
        log("debug", "Inverter inner data successfully decoded");

        // data from solarman frame header
        data.loggerInfo = {};
        data.loggerInfo.frameType = solarman.readUint8(0x00);
        data.loggerInfo.dataType = solarman.readUint8(0x01);
        data.loggerInfo.totalWorkingTime = solarman.readUInt32LE(0x02);            // seconds
        data.loggerInfo.powerOnTime = solarman.readUInt32LE(0x06);                 // seconds
        data.loggerInfo.offsetTime = solarman.readUInt32LE(0x0A);                  // seconds
        data.loggerInfo.totalOperationTime = data.loggerInfo.totalWorkingTime - data.loggerInfo.powerOnTime; // seconds
        log("debug", "solarman frame header successfully decoded");

        data.timestamp = data.loggerInfo.totalWorkingTime + data.loggerInfo.offsetTime;       // timestamp in seconds
        log("info", "data successfully decoded");
    } catch (error) {
        // node.warn(error);
        log("warn", "decode data failed");
        return null;
    };

    return data;
}

function decodeInverterStatus(status) {
    switch (status) {
        case 0x00:
            return `standby`;
        case 0x02:
            return `normal`;
        case 0x03:
            return `fault`;
        case 0x04:
            return `permanent`;

        default:
            return `0x${`0${(status.toString(16))}`.slice(-2)}`;
    }
}

function inputModeDecoder(mode) {
    switch (mode) {
        case 0x00:
            return 'parallel';
        case 0x01:
            return 'independent';

        default:
            return `0x${`0${(mode.toString(16))}`.slice(-2)}`;
    }
}

function countryDecoder(countryCode) {
    switch (countryCode) {
        case 0:
            return "Germany VDE AR-N4105";
        case 1:
            return "Italy CE10-21";
        case 2:
            return "Australia";
        case 3:
            return "Spain RD1699";
        case 4:
            return "Turkey";
        case 5:
            return "Denmark";
        case 6:
            return "Greece Continent";
        case 7:
            return "Netherland";
        case 8:
            return "Belgium";
        case 9:
            return "UK-G59";
        case 10:
            return "China";
        case 11:
            return "France";
        case 12:
            return "Poland";
        case 13:
            return "Germany BDEW";
        case 14:
            return "Germany VDE 0126";
        case 15:
