    }
}
/**
 * read faults(5) every fault is UInt16BE
 * every bit in fault is an error if bit = 1, 16 errors in one fault
 * @param {Buffer} errorBuffer 5 faults 2bytes each = 10bytes long buffer
 * @return array of errors
 */
function errorDecoder(errorBuffer) { // 10bytes in hex
    const errors = [
        [
            "ID09 - PVOVP(PV Over Voltage Protection)",
            "ID10 - IpvUnbalance(PV Input Current Unbalance)",
            "ID11 - PvConfigSetWrong(PV Input Mode Configure wrong)",
            "ID12 - GFCIFault(Ground - Fault circuit interrupters Fault)",
            "ID13 - PhaseSequenceFault(Phase sequence Fault)",
            "ID14 - HwBoostOCP(hardware boost over current protection)",
            "ID15 - HwAcOCP(Hardware AC over current protection)",
            "ID16 - AcRmsOCP(The Grid current is too high)",
        ], [
            "ID01 - GridOVP(grid over voltage protection)",
            "ID02 - GridUVP(grid under voltage protection)",
            "ID03 - GridOFP(grid over frequency protection)",
            "ID04 - GridUFP(grid under frequency protection)",
            "ID05 - PVUVP(PV Under Voltage Protection)",
            "ID06 - GridLVRT(Grid Low Voltage Ride through)",
            "ID07 - reserved",
            "ID08 - reserved",
        ], [
            "ID25 - BusUVP(Bus under voltage protection)",
            "ID26 - BusOVP(Bus over voltage protection)",
            "ID27 - VbusUnbalance(Bus voltage unbalance)",
            "ID28 - DciOCP(The DCI is too high)",
            "ID29 - SwOCPInstant(The Grid current is too high)",
            "ID30 - SwBOCPInstant(The input current is too high)",
            "ID31 - reserved",
            "ID32 - reserved",
        ], [
            "ID17 - HwADFaultIGrid(The Grid current sampling is error)",
            "ID18 - HwADFaultDCI(The DCI sampling is error)",
            "ID19 - HwADFaultVGrid(The Grid voltage sampling is error)",
            "ID20 - GFCIDeviceFault(GFCI device sampling is error)",
            "ID21 - MChip_Fault(Main chip fault)",
            "ID22 - HwAuxPowerFault(Hardware auxiliary power fault)",
            "ID23 - BusVoltZeroFault(Bus voltage zero fault)",
            "ID24 - IacRmsUnbalance(The output current is not balanced)",
        ], [
            "ID41",
            "ID42",
            "ID43",
            "ID44",
            "ID45",
            "ID46",
            "ID47",
            "ID48",
        ], [
            "ID33",
            "ID34",
            "ID35",
            "ID36",
            "ID37",
            "ID38",
            "ID39",
            "ID40",
        ], [
            "ID57 - OverTempFault_Inv(The inverter temp is too high)",
            "ID58 - OverTempFault_Boost(The boost temp is too high)",
            "ID59 - OverTempFault_Env(The environment temp is too high)",
            "ID60 - PEConnectFault(The inverter is not connect the PE wire)",
            "ID61 - reserved",
            "ID62 - reserved",
            "ID63 - reserved",
            "ID64 - reserved",

        ], [
            "ID49 - ConsistentFault_VGrid(The grid voltage sampling value between the master and slave DSP is Vary widely)",
            "ID50 - ConsistentFault_FGrid(The grid frequency sampling value between the master and slave DSP is Vary widely)",
            "ID51 - ConsistentFault_DC(The DCI sampling value between the master and slave DSP is Vary widely)",
            "ID52 - ConsistentFault_GFCI(The GFCI sampling value between the master and slave DSP is Vary widely)",
            "ID53 - SpiCommLose(The communication between the master and slave DSP is fail)",
            "ID54 - SciCommLose(The communication between the slave and communication board is fail)",
            "ID55 - RelayTestFail(The relay is faulty)",
            "ID56 - PvIsoFault(The insulation resistance between the PV array and the earth is too low)",

        ], [
            "ID73 - reserved",
            "ID74 - unrecoverIPVInstant(The input current is too high.and has cause unrecoverable fault)",
            "ID75 - unrecoverWRITEEEPROM(The EEPROM is faulty)",
            "ID76 - unrecoverREADEEPROM(The EEPROM is faulty)",
            "ID77 - unrecoverRelayFail(The relay is faulty, and has cause unrecoverable fault)",
            "ID78 - reserved",
            "ID79 - reserved",
            "ID80 - reserved",
        ], [
            "ID65 - unrecoverHwAcOCP(The grid current is too high,and has cause unrecoverable fault)",
            "ID66 - unrecoverBusOVP(The bus voltage is too high,and has cause unrecoverable fault)",
            "ID67 - unrecoverIacRmsUnbalance(The grid current is unbalance,and has cause unrecoverable fault)",
            "ID68 - unrecoverIpvUnbalance(The input current is unbalance,and has cause unrecoverable fault)",
            "ID69 - unrecoverVbusUnbalance(The bus voltage is unbalance,and has cause unrecoverable fault)",
            "ID70 - unrecoverOCPInstant(The grid current is too high,and has cause unrecoverable fault)",
            "ID71 - unrecoverPvConfigSetWrong(PV Input Mode Configure wrong,and has cause unrecoverable fault)",
            "ID72 - reserved",

        ],
    ];
    let currentErrors = [];
    for (let byteNr = 0; byteNr < errorBuffer.length / 2; byteNr += 2) {
        const faultBuffer = errorBuffer.subarray(byteNr, byteNr + 2);
        for (let faultByte = 0; faultByte < faultBuffer.length; faultByte++) {
            const byteData = faultBuffer.readUint8(faultByte);
            const byteDataStr = `0000000${byteData.toString(2)}`.slice(-8); // byte data 8 bits string
            let bitArray = Array.from(byteDataStr, x => Number.parseInt(x, 2));
            bitArray.reverse(); // flip array so bit 0 is @ bitArray[0]
            let bitErrors = errors[byteNr + faultByte].filter((element, index) => {
                return bitArray[index] == 1;
            });
            currentErrors.push(bitErrors);
        }
    }
    return currentErrors.flat();

}

function log(level, message) {
    switch (level) {
        case "warn":
            if (env.get("logLvl") >= 1) { node.warn("[w] " + message); }
            break;
        case "info":
            if (env.get("logLvl") >= 2) { node.log("[i] " + message); }
            break;
        case "debug":
            if (env.get("logLvl") >= 3) { node.log("[d] " + message); }
            break;
    }
}

if (typeof msg.debug === 'undefined') { msg.debug = {} };
msg.debug.header = decodeHeader(msg.payload.header);
flow.set('requestNr', (msg.debug.header.requestNr >= 255) ? 1 : (msg.debug.header.requestNr + 1));
flow.set('responseNr', msg.debug.header.responseNr);
return msg;

function decodeHeader(buffer) {
    const header = {};
    header.dataLength = buffer.readUInt16LE(1);
    header.requestNr = buffer.readUInt8(5);
    header.responseNr = buffer.readUInt8(6);
    header.loggerSN = buffer.readUInt32LE(7);
    return header;
}

msg.payload = checkFrame(msg.payload);
msg.debug = {};
msg.debug.frame = msg.payload;
msg.topic = "frame";
msg.timestamp = Date.now(); // timestamp in ms
return msg;

function checkFrame(frameBuffer) {
    log("debug", `checking frame buffer(${frameBuffer.length})`);
    const headerSize = 11;
    const footerSize = 2;
    if (frameBuffer.length < (headerSize + footerSize)) {
        log("warn", `response frame too short(got: ${frameBuffer.length}, minimum: ${headerSize + footerSize})`);
        return frameBuffer;
    }
    const frame = {};
    const frameDataLength = frameBuffer.readUInt16LE(1);
    const calculatedLength = headerSize + frameDataLength + footerSize;
    if (frameBuffer.length > calculatedLength) {
        if (frameBuffer[calculatedLength - 1] === 0x15) {
            frame.length = calculatedLength;
            log("warn", `extra data after frame end(${frame.length}), +${frameBuffer.length - calculatedLength})`);
            frame.extraData = checkFrame(frameBuffer.subarray(calculatedLength));
        } else {
            log("warn", `frame longer than expected(got: ${frame.length}, expected: ${calculatedLength})`);
            frame.length = frameBuffer.length;
        }
    } else if (frameBuffer.length < calculatedLength) {
        log("warn", `frame shorter than expected(got: ${frame.length}, expected: ${calculatedLength})`);
        frame.length = frameBuffer.length;
    } else {
        log("info", `frame length OK(${calculatedLength})`);
        frame.length = calculatedLength;
    }
    frame.isValidV5frame = frameBuffer[0] === 0xA5 && frameBuffer[frame.length - 1] === 0x15;
    if (!frame.isValidV5frame) { log("warn", "invalid V5 frame"); }
    else { log("info", "valid V5 frame"); }
    const calculatedChecksum = frameChecksum(frameBuffer, frame.length);
    const checksum = frameBuffer.readUInt8(frame.length - footerSize);
    const data = frameBuffer.subarray(headerSize, frame.length - footerSize);
    if (calculatedChecksum === checksum) {
        frame.frameChecksum = "OK";
        log("info", "frame checksum OK");
    } else {
        frame.frameChecksum = "mismatch";
        log("warn", "frame checksum mismatch");
    }
    frame.buffer = frameBuffer;
    frame.header = frameBuffer.subarray(0, 11);
    frame.data = checkData(data);
    frame.footer = frameBuffer.subarray(frame.length - 2, frame.length);
    frame.timestamp = Math.floor(Date.now() / 1000); // unixtimestamp in seconds
    return frame;
}

function frameChecksum(frame, length) {
    frame = frame.subarray(1, length - 2);
    let sum = 0x0;
    for (const byte of frame) {
        sum += byte;
    }
    sum = sum % 256;
    return sum;
}

function checkData(dataBuffer) {
    log("debug", `checking data buffer(${dataBuffer.length})`);
    const solarmanHeaderLength = 14;
    if (dataBuffer.length >= solarmanHeaderLength) {
        const data = {};
        data.solarmanHeader = dataBuffer.subarray(0, solarmanHeaderLength);
        if (dataBuffer.length === solarmanHeaderLength) {
            log("warn", "frame data empty");
            return data
        }
        log("debug", "data buffer OK");
        data.modbusFrame = dataBuffer.subarray(solarmanHeaderLength);
        data.modbusFrameCrc = checkModbusFrame(data.modbusFrame);
        return data;
    } else if (dataBuffer.length === 1 && dataBuffer[0] === 0x00) {
        log("warn", "null data");
        return dataBuffer
    } else {
        log("warn", `data too short(got: ${dataBuffer.length}, minimum: ${solarmanHeaderLength})`);
        return dataBuffer
    }
}

function checkModbusFrame(frame) {
    log("debug", `checking modbus frame(${frame.length})`);
    let modbusFrameCrc = undefined;
    const headerLength = 3; // first 3 bytes are: slave address, function code, data length
    const crcLength = 2; // int 16 LE
    if (frame.length >= (headerLength + crcLength)) {
        const calculatedCrc = crc16modbus(frame.subarray(0, frame.length - crcLength));
        const crc = frame.readUInt16LE(frame.length - crcLength);
        if (calculatedCrc === crc) {
            modbusFrameCrc = "OK";
            log("info", "modbus frame crc OK");
            const registersDataLength = frame[2]; // data length
            const calculatedDataLength = frame.length - (headerLength + crcLength);
            if (frame.length === (headerLength + crcLength)) {
                log("warn", "modbus frame data empty");
            } else if (registersDataLength !== calculatedDataLength) {
                log("warn", `wrong modbus registers data length(got: ${calculatedDataLength}, expected: ${registersDataLength})`);
            } else {
                log("debug", `modbus registers data length OK(${registersDataLength})`);
            }
        } else {
            modbusFrameCrc = "mismatch";
            log("warn", "modbus frame crc mismatch");
        }
    } else {
        log("warn", `modbus frame too short(got: ${frame.length}, minimum: ${headerLength + crcLength})`);
    }
    return modbusFrameCrc;
}

function log(level, message) {
    switch (level) {
        case "warn":
            if (env.get("logLvl") >= 1) { node.warn("[w] " + message); }
            break;
        case "info":
            if (env.get("logLvl") >= 2) { node.log("[i] " + message); }
            break;
        case "debug":
            if (env.get("logLvl") >= 3) { node.log("[d] " + message); }
            break;
    }
}
